var approval = new GlideRecord('sysapproval_approver');
approval.addQuery('document_id', current.sys_id);
approval.addQuery('sys_created_by', gs.getUserName());
approval.orderByDesc('sys_created_on');
approval.setLimit(1);
approval.query();
if (approval.next()) {
	var gr = new GlideRecord("sys_journal_field");
	gr.addQuery("name","sysapproval_approver");
	gr.addQuery("sys_created_by",gs.getUserName());
	gr.addQuery("element_id", approval.sys_id);
	gr.orderByDesc("sys_created_on");
	gr.setLimit(1);
	gr.query();
	if(gr.next()) {
		current.setValue("rejected_reason",gr.value);
	}
}

