// Populating the approver list
answer = populateApproverList();

function populateApproverList() {
    var userId;
    var gr = new GlideRecord('sys_user_grmember');
	if(current.table_name == "sn_vul_container_image_vulnerable_item")
		gr.addEncodedQuery('group=43f2c22cc30541109e785fe51840dd56');
	else
		gr.addEncodedQuery('group=5c58a612c7228010697aced603c260e7');
    gr.setLimit(1);
    gr.query();
    if (gr.hasNext()) {
        userId = gs.getUserID();
        var approverList = current.approvers_list + ',' + userId;
        current.approvers_list = approverList;
    }
}

